import React from 'react'

const Button = ({buttonText, reqType , setReqType}) => {
  return (
    <button className= {buttonText === reqType ? "selected" : null}>
     {buttonText}
     type="button"
            onClick={() => setReqType(buttonText)}
    </button>
  )
}

export default Button
